import axios from 'axios';

const getPosts = (query) => {
    try {
        axios.get('http://localhost:3001/api/posts')
        .then(response => {
            const res = response.json()
            return res;
        })
    } catch (err) {
        console.log(err)
    }
}
export default getPosts;
